import React, { useState } from "react";
import SearchBar from "./SearchBar";
import CelebrityDetails from "./CelebrityDetails";
import celebrityData from "./celebrities.json";

const mystyle = {
  color: "white",
  backgroundColor: "DodgerBlue",
  padding: "10px",
  fontFamily: "Arial",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  justifyContent: "center",
  gap: "50px",
};

const searchContainerStyle = {
  backgroundColor: "#fff",
  borderRadius: "5px",
  padding: "20px",
  margin: "20px 0",
  display: "flex",
  justifyContent: "center",
};

const inputStyle = {
  padding: "10px",
  border: "1px solid #ccc",
  borderRadius: "5px",
  marginRight: "10px",
};

const buttonStyle = {
  padding: "10px 20px",
  border: "none",
  borderRadius: "5px",
  backgroundColor: "#007bff",
  color: "#fff",
  cursor: "pointer",
};

const resultsContainerStyle = {
  backgroundColor: "#fff",
  borderRadius: "5px",
  padding: "20px",
  margin: "20px 0",
};

function Star() {
  const [selectedCelebrity, setSelectedCelebrity] = useState(null);

  const handleSearch = (searchTerm) => {
    const celebrity = celebrityData.find(
      (celeb) => celeb.name.toLowerCase() === searchTerm.toLowerCase()
    );

    if (celebrity) {
      setSelectedCelebrity(celebrity);
    } else {
      setSelectedCelebrity(null);
    }
  }

  return (
    <div style={mystyle}>
      <div style={searchContainerStyle} className="search-container">
        <input type="text" style={inputStyle} />
        <button style={buttonStyle}>Search</button>
      </div>
      <div style={resultsContainerStyle} className="results-container">
        {selectedCelebrity ? (
          <CelebrityDetails celebrity={selectedCelebrity} />
        ) : (
          <p className="no-results">No results found</p>
        )}
      </div>
    </div>
  );
}

export default Star;
