import React, { useState } from 'react';
import { TextField, Button, Typography, Paper, Grid } from '@mui/material';

const Star = () => {
  const [celebrityName, setCelebrityName] = useState('');
  const [height, setHeight] = useState('');
  const [weight, setWeight] = useState('');
  const [bmi, setBMI] = useState(null);
  const [dietPlan, setDietPlan] = useState('');
  const [exercises, setExercises] = useState('');

  const handleFetchData = () => {
    // Calculate BMI based on height and weight
    const heightInMeters = parseFloat(height) / 100;
    const weightInKg = parseFloat(weight);

    if (heightInMeters <= 0 || weightInKg <= 0) {
      alert('Please enter valid height and weight.');
      return;
    }

    const bmiValue = weightInKg / (heightInMeters * heightInMeters);
    setBMI(bmiValue.toFixed(1));

    // Determine exercises based on BMI range
    let exerciseSuggestions = '';
    if (bmiValue < 18.5) {
      exerciseSuggestions = 'Yoga and light cardio are recommended.';
    } else if (bmiValue >= 18.5 && bmiValue < 25) {
      exerciseSuggestions = 'Running and strength training are recommended.';
    } else {
      exerciseSuggestions = 'Weight lifting and intense cardio are recommended.';
    }

    setDietPlan('Eat healthy and stay hydrated.');
    setExercises(exerciseSuggestions);
  };

  return (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <Typography variant="h4" gutterBottom>
          Celebrity Diet and Exercises
        </Typography>
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          label="Enter Celebrity Name"
          variant="outlined"
          fullWidth
          value={celebrityName}
          onChange={(e) => setCelebrityName(e.target.value)}
        />
      </Grid>
      <Grid item xs={6} sm={3}>
        <TextField
          label="Height (cm)"
          variant="outlined"
          fullWidth
          type="number"
          value={height}
          onChange={(e) => setHeight(e.target.value)}
        />
      </Grid>
      <Grid item xs={6} sm={3}>
        <TextField
          label="Weight (kg)"
          variant="outlined"
          fullWidth
          type="number"
          value={weight}
          onChange={(e) => setWeight(e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <Button
          variant="contained"
          color="primary"
          onClick={handleFetchData}
        >
          Calculate BMI and Get Exercises
        </Button>
      </Grid>
      {bmi !== null && (
        <Grid item xs={12} sm={6}>
          <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
            <Typography variant="h6">BMI:</Typography>
            <Typography>{bmi}</Typography>
          </Paper>
        </Grid>
      )}
      {dietPlan && exercises && (
        <Grid item xs={12} sm={6}>
          <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
            <Typography variant="h6">Diet Plan:</Typography>
            <Typography>{dietPlan}</Typography>
            <Typography variant="h6" style={{ marginTop: '10px' }}>
              Exercises:
            </Typography>
            <Typography>{exercises}</Typography>
          </Paper>
        </Grid>
      )}
    </Grid>
  );
};

export default Star;
